import React, { useRef, useEffect } from "react";
import * as d3 from "d3";

const PieChart = () => {
  const ref = useRef();

  useEffect(() => {
    const data = [
      { category: "Good", percentage: 43.51 },
      { category: "Moderate", percentage: 38.68 },
      { category: "Unhealthy", percentage: 9.07 },
      { category: "Unhealthy for Sensitive Groups", percentage: 6.92 },
      { category: "Very Unhealthy", percentage: 1.09 },
      { category: "Hazardous", percentage: 0.73 },
    ];

    const color = d3.scaleOrdinal([
      "#4daf4a",
      "#377eb8",
      "#ff7f00",
      "#BF8B67",
      "#9D5353",
      "#632626",
    ]);
    const pie = d3.pie().value((d) => d.percentage);

    const width = 500;
    const height = 500;
    const radius = Math.min(width, height) / 2;

    const arc = d3.arc().outerRadius(radius).innerRadius(0);

    const svg = d3
      .select(ref.current)
      .attr("class", "pie")
      .attr("width", width)
      .attr("height", height);

    const g = svg
      .append("g")
      .attr("transform", `translate(${width / 2}, ${height / 2})`);

    const arcs = g
      .selectAll("arc")
      .data(pie(data))
      .enter()
      .append("g")
      .attr("class", "arc");

    arcs
      .append("path")
      .attr("d", arc)
      .attr("fill", (d) => color(d.data.category));

    arcs
      .append("text")
      .attr("transform", (d) => `translate(${arc.centroid(d)})`)
      .attr("text-anchor", "middle")
      .text((d) => d.data.category);

    svg
      .append("text")
      .attr("x", width / 2)
      .attr("y", height + 10)
      .style("text-anchor", "middle")
      .style("font-size", "18px")
      .style("font-weight", "bolder")
      .text("Top 10 cities with the highest AQI value in USA");
  }, []);

  return <svg ref={ref}></svg>;
};

export default PieChart;
