import React, { useEffect, useRef } from "react";
import * as d3 from "d3";

import jsonData from "./data.json";

import "./index.css";

function ChartOne() {
  const chartRef = useRef(); // 创建一个ref用于选择DOM元素
  useEffect(() => {
    const margin = { top: 20, right: 20, bottom: 60, left: 60 };
    const width = 700 - margin.left - margin.right;
    const height = 300 - margin.top - margin.bottom;

    const svg = d3
      .select(chartRef.current)
      .attr("width", width + margin.left + margin.right)
      .attr("height", height + margin.top + margin.bottom)
      .append("g")
      .attr("transform", `translate(${margin.left}, ${margin.top})`);

    // Create scales
    const xScale = d3
      .scaleBand()
      .domain(jsonData.map((d) => d.city))
      .range([0, width])
      .padding(0.1);

    const yScale = d3
      .scaleLinear()
      .domain([0, d3.max(jsonData, (d) => d["CO AQI Value"])])
      .range([height, 0]);

    const bars = svg
      .selectAll(".bar-group")
      .data(jsonData)
      .join("g")
      .attr("class", "bar-group")
      .attr("transform", (d) => `translate(${xScale(d.city)}, 0)`); // 移动整个组到正确的位置

    const barWidth = xScale.bandwidth(); // 柱宽

    // CO bar bar
    bars
      .selectAll(".bar-co")
      .data((d) => [d])
      .join("rect")
      .attr("class", "bar-co")
      .attr("x", barWidth)
      .attr("y", (d) => yScale(d["CO AQI Value"]))
      .attr("width", barWidth)
      .attr("height", (d) => height - yScale(d["CO AQI Value"]))
      .attr("text-anchor", "middle")
      .text((d) => d["CO AQI Value"]);

    bars
      .selectAll(".label-co")
      .data((d) => [d])
      .join("text")
      .attr("class", "label-co")
      .attr("x", barWidth + barWidth / 2)
      .attr("y", (d) => yScale(d["CO AQI Value"]) - 2)
      .attr("text-anchor", "middle")
      .text("CO");

    svg
      .append("g")
      .attr("transform", `translate(0, ${height})`)
      .call(d3.axisBottom(xScale));

    svg
      .append("text")
      .attr("transform", `translate(${width / 2}, ${height + margin.top + 20})`) // 将标签放置在 x 轴下方适当的位置
      .style("text-anchor", "middle")
      .text("Global urban pollution situation");

    svg.append("g").call(d3.axisLeft(yScale));
  }, []);

  return <svg ref={chartRef} />;
}

export default ChartOne;
