import React, { useEffect, useRef } from "react";
import * as d3 from "d3";
import { feature } from "topojson-client";
import us from "../Data/usa-states-10m.json";
// import data from "./data.json";

const data = [
  {
    country: "New York",
    city: "New York",
    "CO AQI Value": 4,
    coordinates: [-74.0059, 40.7128],
  },
  {
    country: "United States of America",
    city: "Los Angeles",
    "CO AQI Value": 2,
    coordinates: [-118.2437, 34.0522],
  },
  {
    country: "Russian Federation",
    city: "huston<-my babe is here",
    "CO AQI Value": 6,
    coordinates: [-95.3698, 29.7604],
  },
  {
    country: "Russian Federation",
    city: "damfo",
    "CO AQI Value": 6,
    coordinates: [-104.9903, 39.7392],
  },
];

function USAMap() {
  const ref = useRef();
  const width = 700;
  const height = 500;

  useEffect(() => {
    const svg = d3.select(ref.current);
    svg.selectAll("*").remove(); // 添加这行代码，移除svg内部的所有元素

    const projection = d3
      .geoAlbersUsa()
      .fitSize([width, height], feature(us, us.objects.states));

    const path = d3.geoPath().projection(projection);

    svg
      .append("g")
      .selectAll("path")
      .data(feature(us, us.objects.states).features)
      .join("path")
      .attr("d", path)
      .attr("stroke", "#000")
      .attr("fill", "gray");

    // 添加城市点
    svg
      .selectAll("circle")
      .data(data)
      .enter()
      .append("circle")
      .attr("cx", function (d) {
        return projection(d.coordinates)?.[0];
      })
      .attr("cy", function (d) {
        return projection(d.coordinates)?.[1];
      })
      .attr("r", "8px")
      .attr("fill", function (d) {
        return d["CO AQI Value"] > 4 ? "yellow" : "red";
      });

    // 添加城市名称
    svg
      .selectAll(".city-label")
      .data(data.filter((d) => d["CO AQI Value"] > 4))
      .enter()
      .append("text")
      .attr("class", "city-label")
      .attr("x", function (d) {
        return projection(d.coordinates)[0];
      })
      .attr("y", function (d) {
        return projection(d.coordinates)[1];
      })
      .text(function (d) {
        return d.city;
      })
      .attr("font-size", "12px")
      .attr("font-weight", "bold")
      .attr("text-anchor", "middle")
      .attr("fill", "black");
  }, []);

  return <svg ref={ref} style={{ width: width, height: height }} />;
}

export default USAMap;
